import axios from "axios";
import React, { useState } from 'react';

function CommentsViewer() {
  const [url, setUrl] = useState("");
  const [respond, setRespond] = useState("");

  const submitForm = async (e) => {
    e.preventDefault(); // stop page reload
    try {
      const response = await axios.post('http://localhost:5000/scrape/', { url });
      setRespond(response.data);
      alert("The URL has been submitted");
    } catch (error) {
      console.error(error);
    }
    console.log(url);
  };

  return (
    <div >
      <div className="urlForm">
        <form onSubmit={submitForm}>
          <input
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            placeholder="paste the URL"
          />
          <button type="submit">Start</button>
        </form>
      </div>

      <div className="CommentsViewField">
        {respond && (
          <div>
            <h2>{respond.title}</h2>
            {/* */}
            <ul>
              {respond.comments && respond.comments.map((c) => (
                <li key={c.index}>
                  <strong>{c.likes} üëç</strong> ‚Äî {c.text}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default CommentsViewer;
